SELECT  qTable.dept, qTable.sales, qTable.overhead, qTable.sales-qTable.overhead AS profits,  SUM( qTable.sales - qTable.overhead ) as totProfit  FROM ( SELECT  bamazon_db.departments.department_name as dept,  bamazon_db.products.product_sales as sales, bamazon_db.departments.over_head_costs as overhead  FROM bamazon_db.products RIGHT JOIN bamazon_db.departments  ON bamazon_db.products.department_name = bamazon_db.departments.department_name ORDER BY  bamazon_db.departments.department_name ORDER BY bamazon_db.departments.department_name ) AS qTable  GROUP BY qTable.dept 